import java.util.Arrays;



public class Min_Max_arr {
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int[] A = { 5, 7, 2, 4, 9, 6 };
		 
	      Arrays.sort(A);
	      
	      System.out.println("min ele " + A[0]);
	      
	      System.out.println("max ele " + A[A.length - 1]);

	}

}
